INSERT INTO `meo` VALUES (1,'6 skills trong căn bếp bữa ăn ngon','Chỉ cần nắm vững những kỹ năng này, trình nấu nướng của bạn sẽ được nâng cấp, dù thực tế bạn không quá thích vào bếp hay nấu ăn giỏi.','/image/6skillslambep/bokho.png','/MeoVat/6-Skills-lam-bep','6-Skills-lam-bep'),(2,'6 bí kíp chọn thực phẩm tươi ngon','Với 10 mẹo nhận biết dưới đây, bạn dễ dàng loại bỏ những loại thực phẩm không an toàn. Đảm bào sức khỏe cho bản thân và gia đình.','/image/10bikipchonthucphamtuoi/trungga.jpg','/MeoVat/10-bi-kip-chon-thuc-pham-tuoi','10-bi-kip-chon-thuc-pham-tuoi'),(3,'Cách làm sạch ấm đun nước điện','Tần suất làm sạch sẽ phụ thuộc vào tần suất sử dụng ấm nước của bạn.Bên ngoài nên được lau loại bỏ các vết ố và bẩn tung tóe ít nhất một lần một tuần','/image/lamsachamdunnuoc/1.jpg','/MeoVat/Cach-lam-sang-am-dun-nuoc-dien','Cach-lam-sang-am-dun-nuoc-dien'),(4,'6 mẹo nấu ăn hữu ích bạn có thể học lỏm từ đầu bếp nhà hàng','Cắt lát chanh càng mỏng, bạn sẽ cắt được lát chanh càng chua','/image/9meocooking/meo3-6308-1506331373.jpg','/MeoVat/9-meo-nau-an-huu-ich','6-meo-don-gian'),(5,'6 lưu ý nấu ăn đơn giản','Cho người đầu bếp mới bắt đầu, 6 sai lầm nấu ăn phổ biến mà mọi người thường mắc phải và cách mà bạn có thể tránh chúng','https://hotelcareers.vn/wp-content/uploads/2022/07/meo-vat-nau-an.jpg','/MeoVat/6-luu-y-cho-nguoi-moi-bat-dau','6-luu-y-cho-nguoi-moi-bat-dau'),(6,'Hướng dẫn nấu ăn cùng các loại ngũ cốc','Nếu bạn muốn bổ sung thêm nhiều ngũ cốc nguyên cám vào chế độ ăn uống thì bạn có thể tìm hiểu hướng dẫn nấu ăn với ngũ cốc đơn giản này','https://cdn.tgdd.vn/2020/12/CookProduct/thumb1-1200x676-11.jpg','/MeoVat/Nau-an-voi-ngu-coc','Nau-an-voi-ngu-coc'),(7,'4 mẹo biến thịt dai thành mềm mại','Những thủ thuật này được các đầu bếp có thâm niên đúc kết ra, có thể chữa thịt dai trở nên mềm mại, ngon tuyệt','https://s1.media.ngoisao.vn/news/2023/04/06/heo-dai-3-ngoisaovn-w1200-h675.jpg','/MeoVat/4-meo-bien-thit-dai-thanh-thit-mem','4-meo-bien-thit-dai-thanh-thit-mem'),(8,'Công thức nấu ăn và cháo ngon của đầu bếp Singapore','Cho gạo đã vo sạch để qua đêm sẽ giúp nấu cơm hoặc cháo đỡ dính và nhanh hơn','https://img-global.cpcdn.com/recipes/466d4da831efb91b/1200x630cq70/photo.jpg','/MeoVat/Cong-thuc-nau-chao-cua-dau-bep-Singapore','chao-ech-singapore'),(9,'Mẹo khử mùi tanh của cá','Mùi tanh là một trong những vấn đề cực kì phổ biến trong các món ăn có chứa cá, hãy cùng 14cooking giải quyết vấn đề này','https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/5/13/909026/186107386_1937994386.jpg',NULL,'catanh');
